import React from "react";
import { useDispatch } from "react-redux";
import { createOrderGhn } from "../../../../../actions/GhnAction";
import {
  deleteOrder,
  getAllOrder,
  ShippingOrder,
} from "../../../../../actions/OrderAction";
import { formatPrice, formatDateOrderPaypal } from "../../../../../utils/index";
import { PaidOrder } from "../../../../../actions/OrderAction";

function Order(props) {
  const { order } = props;
  const dispatch = useDispatch();

  const {
    orderItems,
    totalPrice,
    paymentMethod,
    cancelOrder,
    shippingAddress,
    status,
    paymentResult,
  } = order;

  const handleShippingOrder = async (order) => {
    await dispatch(createOrderGhn(order._id));
    await dispatch(ShippingOrder(order._id));

    dispatch(getAllOrder());
  };

  const handlePayOrder = async (order) => {
    await dispatch(PaidOrder(order.order_code));
    dispatch(getAllOrder());
  };

  const handleDeleteOrder = async (order) => {
    await dispatch(deleteOrder(order._id));
    dispatch(getAllOrder());
  };

  return (
    <>
      <div className="order-list">
        <div className="order-list-items">
          {orderItems.map((item) => (
            <div className="order-items-item">
              <span className="img">
                <img src={item.image} alt=""></img>
              </span>
              <span className="qty">Qty: {item.qty}</span>
              <span className="name">{item.name}</span>
              <span className="price">{formatPrice(item.salePrice)}</span>
            </div>
          ))}
        </div>
        <div className="toatalPrice">
          <span>Tổng tiền: {formatPrice(totalPrice)}</span>
        </div>
        <div className="order-info">
          <div className="order-info-address">
            <b>Địa chỉ : </b>
            {shippingAddress.name},{shippingAddress.province},{" "}
            {shippingAddress.district},{shippingAddress.ward},{" "}
            {shippingAddress.detail},{shippingAddress.phone}
          </div>
        </div>

        {paymentResult ? (
          <div className="order-payment-check">
            Paid : {formatDateOrderPaypal(paymentResult.update_time)}
          </div>
        ) : (
          ""
        )}

        <div className="order-bottom">
          {status === "shipping" ? (
            <div className="order-status">
              <span>
                Đã xác nhận
                {paymentMethod === "payOnline" ? (
                  <span>& Đã thanh toán</span>
                ) : (
                  ""
                )}
              </span>
            </div>
          ) : (
            ""
          )}

          {status === "paid" ? (
            <div className="order-status">
              <span>Đã hoàn thành</span>
            </div>
          ) : (
            ""
          )}

          <div className="order-button">
            {status === "pendding" && cancelOrder === false ? (
              <>
                <button
                  className="shipping"
                  onClick={() => handleShippingOrder(order)}
                >
                  Xác nhận đơn hàng
                </button>
              </>
            ) : (
              ""
            )}
            {status === "shipping" && (
              <>
                <button
                  className="shipping"
                  onClick={() => handlePayOrder(order)}
                >
                  Hoàn thành
                </button>
              </>
            )}

            {cancelOrder === true ? (
              <>
                <span> Khách yêu cầu hủy đơn </span>
                <button
                  className="shipping"
                  onClick={() => handleDeleteOrder(order)}
                >
                  Hủy đơn
                </button>
              </>
            ) : (
              ""
            )}
          </div>
        </div>
      </div>
    </>
  );
}

export default Order;
